import React,{ useState} from 'react';
import { useNavigate } from "react-router-dom";
import {toast} from "react-toastify";
export default function Login() {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const navigate = useNavigate();
    const adminLogin = () => {
        if (!email || !password) {
            toast.error("Please provide value into each input field");
        }else {
            navigate("/admin",).then(() => {
                console.log("success");
            }).catch((err) => toast.error(err.response.data));
            toast.success("Admin Successfully Login");
        }
    };
    return (
        <div>
            <div className="container">
                <div className="row">
                <div className="col-md-6 offset-md-3">
            <form className="align-item-center container">
                <div className="form-group">
                    <label for="inputEmail">Email</label>
                    <div className="col-sm-3" >
                        <input type="email" className="form-control" id="inputEmail" onChange={(event) => {
                            setEmail(event.target.value);
                        }} />
                    </div>
                </div>
                <div className="form-group">
                    <label for="inputPassword" className="col-sm-2 col-form-label">Password</label>
                    <div className="col-sm-3">
                        <input type="password" className="form-control" id="inputPassword" onChange={(event) => {
                            setPassword(event.target.value);
                        }} />
                    </div>
                    <div className="form-group">
                            <label className="form-check-label row justify-content-center mt-5" for="gridRadios1">
                                Remember Password
                            </label>
                            <input className="col-sm-10 row justify-content-center mt-5" type="checkbox" name="gridRadios" id="gridRadios1" value="option1" />
                        </div>
                </div>
                <div className="d-flex align-items-center flex-column"><button type="submit" className="btn btn primary btn-outline-primary p-2 m-2" onClick={adminLogin}>Login</button></div>
            </form >
            </div>
            </div>
            </div>
        </div>
    )
};
