import React, {Component} from 'react';
import './App.css';
import Register from "./components/Register";
import Welcome from "./components/Welcome";

class App extends Component {
  constructor(props) {
    super(props)

    this.state = {
       isRegistered: false,
       fname: null,
       lname: null,
       father_name: null,
       mother_name: null,
       dob:null,
       contact_no:null,
       country_code: null,      
    };
  }

  formHandler = (event)=> {
    const fname=event.target.fname.value;
    const lname =event.target.lname.value;
    const father_name=event.target.father_name.value;
    const mother_name=event.target.mather_name.value;
    const dob=event.target.dob.value;
    const contact_no=event.target.contact_no.value;
    const country_code=event.target.country_code.value;
    this.setState({fname,lname,father_name,mother_name,dob,contact_no,country_code, isRegistered: true });
    event.preventDefault();
  };

  render() {
    return (
      <div>
      {/* {this.state.isRegistered ? (
      <Welcome fname={this.state.fname} lname={this.state.lname}></Welcome>
      ) : (
      <Register Submit={this.formHandler}></Register>
      )} */}
      if {this.state.isRegistered=false}{
        <Welcome fname={this.state.fname} lname={this.state.lname}></Welcome>
      }
      else{
        <Register Submit={this.formHandler}></Register>
      }
      </div>
    );
  }
}

export default App
