import React,{ useState} from 'react';
import { useNavigate } from "react-router-dom";
import {toast} from "react-toastify";
export default function Login() {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const navigate = useNavigate();
    const adminLogin = () => {
        if (!email || !password) {
            toast.error("Please provide value into each input field");
        }else {
            navigate("/admin",).then(() => {
                console.log("success");
            }).catch((err) => toast.error(err.response.data));
            toast.success("Admin Successfully Login");
        }
    };
    return (
        <div>
            <form className="align-item-center">
                <div className="row justify-content-center mt-5">
                    <label for="inputEmail" className="col-sm-2 col-form-label">Email</label>
                    <div className="col-sm-3" >
                        <input type="email" className="form-control" id="inputEmail" onChange={(event) => {
                            setEmail(event.target.value);
                        }} />
                    </div>
                </div>
                <div className="row justify-content-center mt-5">
                    <label for="inputPassword" className="col-sm-2 col-form-label">Password</label>
                    <div className="col-sm-3">
                        <input type="password" className="form-control" id="inputPassword" onChange={(event) => {
                            setPassword(event.target.value);
                        }} />
                    </div>
                </div>
                <fieldset className="row mb-3">
                    <div className="col-sm-10">
                        <div className="form-check">
                            <label className="form-check-label row justify-content-center mt-5" for="gridRadios1">
                                Remember Password
                            </label>
                            <input className="col-row justify-content-center mt-5" type="checkbox" name="gridRadios" id="gridRadios1" value="option1" />
                        </div>
                    </div>
                </fieldset>
                <div className="d-flex align-items-center flex-column"><button type="submit" className="btn btn primary btn-outline-primary p-2 m-2" onClick={adminLogin}>Login</button></div>
            </form >
        </div>
    )
};
