import React, {Component} from 'react';
import './App.css';
import Register from "./components/Register";
import welcome from "./components/welcome";

class App extends Component {
  constructor(props) {
    super(props)

    this.state = {
       isRegistered:false,
       fname: null,
       lname: null,
       father_name: null,
       mother_name: null,
    }
  }

  formHandler = (event)=> {
    console.log(event.target.fname.value);
    console.log(event.target.lname.value);
    console.log(event.target.father_name.value);
    this.setState({fname,lname});
    event.preventDefault();
  };

  render() {
    return (
      <div>
      {this.state.isRegistered ? <welcome></welcome> : <Register submit={this.formHandler}></Register>}
      </div>
    )
  }
}

export default App
